    //       <option className="optionLine" value="houseware">Houseware</option>
    //       <option className="optionLine" value="entertainment">Entertainment</option>
    //     </select>
    // </div>
    <div>
      <select className="drop-down" value={value} onChange={handleChange}>
        <option value="" disabled selected hidden>Select</option>
        {options.map((option) => (
          <option className="option-line" value={option.value}>{option.label}</option>
        ))}
      </select>
    </div>
  );
};
