import React, {useState} from 'react';
import './checkoutSelector.css';
import { CustomButton } from '../../components/customButton/customButton';
import { ExpenseSelector } from '../expenseSelector/expenseSelector';

export const CheckoutSelector = () => { 
  const [isShown, setIsShown] = useState(false);
  const [id, setId] = useState(0);

  const handleClick = event => {
      setIsShown(true);
      setId(id + 1);
  };
  return (
    <div className="checkout-wrapper">
        Total
        {isShown && <ExpenseSelector key={id} />}
        <CustomButton 
            border="none"
            bgColor="#F0A023"
            color="#FFFFFF"
            fontSize="16px"
            fontWeight="500"
            height = "38px"
            onClick={handleClick}
            radius = "19px"
            width = "125px"
            children = "Add receipt"
        />
  </div>
  );
};
