import React, { useContext, useState } from 'react'
import { GlobalContext } from '../../context/globalState';
import './expenseListItem.css'

export const ExpenseListItem = ({id}) => {
  const [expenseName, setExpenseName] = useState('');
  const [expenseCost, setExpenseCost] = useState(0);

  
  const { addExpense } = useContext(GlobalContext);

  const onChange = (e) => {
    setExpenseCost(e.target.value);
    
    const newExpense = {
      id: id,
      expenseName,
      expenseCost
    };
    
    addExpense(newExpense);
  } 
  

  return (
    <div>
        <div className="input-asd">
            <input type="text" value={expenseName} className="input-text" onChange={(e) => setExpenseName(e.target.value)} placeholder="Expense name"/>
            <input type="number" value={expenseCost} className="input-cost" onChange={onChange} placeholder="â‚¬0.00"/>
        </div>
    </div>
  )
}
