import React, { useState } from 'react';
import './App.css';
import { ExpenseSelector } from './features/expenseSelector/expenseSelector';
import { CustomButton } from './components/customButton/customButton';
import { TotalCount } from './components/totalCount/totalCount';

function App() {

const [isShown, setIsShown] = useState(false);
const [panels, setPanels] = useState([]);

  const handleClick = e => {
    e.preventDefault();
    setIsShown(true);

    const nextId = panels.length + 1;

    setPanels({
      panels: panels.concat([nextId])
    });
  };
  return (
    <div>
      <div>
        {panels.map((panelId) => (
          <ExpenseSelector key={panelId} id={panelId} />
        ))}
      </div>
      {/* {isShown && <ExpenseSelector />} */}
      <div className="checkout-wrapper">
        <TotalCount /> 
        <CustomButton 
          border="none"
          bgColor="#F0A023"
          color="#FFFFFF"
          fontSize="16px"
          fontWeight="500"
          height = "38px"
          onClick={handleClick}
          radius = "19px"
          width = "125px"
          children = "Add receipt"
        />
      </div>
    </div>
  );
}

export default App;
