import React, { useState } from "react";

export const CustomButton = ({ 
    border,
    bgColor,
    color,
    children,
    fontSize,
    fontWeight,
    height,
    onClick, 
    radius,
    width
  }) => { 
    const [isHover, setIsHover] = useState(false);

    const handleMouseEnter = () => {
      setIsHover(true);
    };

    const handleMouseLeave = () => {
      setIsHover(false);
    };

  return (
    <button 
      onClick={onClick}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      style={{
        backgroundColor: isHover ? 'red' : bgColor,
        border,
        borderRadius: radius,
        height,
        width,
        color,
        fontSize,
        fontWeight
      }}
    >
    {children}
    </button>
  );
};
