import React, { useContext } from 'react'
import './totalCount.css'
import { GlobalContext } from '../../context/globalState';

export const TotalCount = () => {
  const { expenses } = useContext(GlobalContext);

  // const result = Object.values(expenses.reduce((acc, cur) => { 
  //   acc[cur.id] = cur;
  //   return acc;
  // }, {}))

  // console.log(result + "result");

  const costs = expenses.map(expense => expense.expenseCost);
  console.log(costs);
  console.log(expenses.indexOf(expenses.id))
  const total = costs.reduce((acc, item) => (acc += item), 0).toFixed(2);

  return (
    <div className="total-wrapper">
        <p className="total">Total</p>
        <p className="total-sum">â‚¬{total}</p>
    </div>
  )
}
