export default (state, action) => {
    switch(action.type) {
      case 'REMOVE_EXPENSE':
        return {
          ...state,
          expenses: state.expenses.filter(expense => expense.id !== action.payload)
        }
      case 'ADD_EXPENSE':
        return {
          ...state,
          expenses: [action.payload, ...state.expenses, console.log(action)],
        }
      default:
        return state;
    }
  }
