import React from 'react';
import './dropDownMenu.css';

export const DropDownMenu = ({value, options, onChange}) => {
  return (
    <div>
      <select className="drop-down" value={value} onChange={onChange}>
        {options.map((option, i) => (
          <option className="option-line" key={i} value={option.value}>{option.label}</option>
        ))}
      </select>
    </div>
  );
};
