import React from 'react';
import './dropDownMenu.css';

export const DropDownMenu = () => {
  const options = [
    { label: 'Fruit', value: 'fruit' },
    { label: 'Vegetable', value: 'vegetable' },
    { label: 'Meat', value: 'meat' },
  ];

  const [value, setValue] = React.useState('fruit');

  const handleChange = (event) => {
    setValue(event.target.value);
  };
  return (
    // <div>
    //     <select className="dropDown" required>
    //       <option className="optionLine" defaultValue="" disabled selected hidden>Select</option>
    //       <option className="optionLine" value="food">Food</option>
    //       <option className="optionLine" value="houseware">Houseware</option>
    //       <option className="optionLine" value="entertainment">Entertainment</option>
    //     </select>
    // </div>
    <div>
      <select value={value} onChange={handleChange}>
        {options.map((option) => (
          <option value={option.value}>{option.label}</option>
        ))}
      </select>
    </div>
  );
};
