import React from "react";

export const CustomButtonComp = ({ 
    border,
    bgColor,
    color,
    children,
    fontSize,
    fontWeight,
    height,
    onClick, 
    radius,
    width
  }) => { 
  return (
    <button 
      onClick={onClick}
      style={{
         backgroundColor: bgColor,
         border,
         borderRadius: radius,
         height,
         width,
         color,
         fontSize,
         fontWeight
      }}
    >
    {children}
    </button>
  );
};
