    //     }
      case 'ADD_EXPENSE':
        return {
          ...state,
          expenses: [action.payload, ...state.expenses,          console.log(expenses)          ],
          expenses: state.expenses.filter(expense => expense.id === action.payload, console.log(expenses))
        }
      default:
